mapper.select:
  	select 
    
	    from admin
	    where isdelete = 0
	  
        and admin_id = #{adminId}
      
      
        and username LIKE CONCAT('%',#{userName},'%') 
      
      
        AND password = #{passWord}
      
       
        and realname  LIKE  CONCAT('%',#{realName},'%') 
      
      
          AND CREATE_TIME > #{beginTime} 
      
      
          AND CREATE_TIME <  #{endTime} 
      
      
        order by ${sidx} ${sord}
