mapper.select:
  	select 
    
	    from privilege
	    where isdelete = 0
	  
        and privilege_id = #{privilegeId}
      
      
        and display_name LIKE CONCAT('%',#{displayName},'%') 
      
      
        AND privilege_name = #{privilegeName}
      
       
        and privilege_code  = #{privilegeCode}
      
       
        and parent_id  = #{parentId}
      
      
          AND create_time > #{beginTime} 
      
      
          AND create_time <  #{endTime} 
      
      
        order by ${sidx} ${sord}
