mapper.select:
  	select 
    
	    from role
	    where isdelete = 0
	  
        and role_id = #{roleId}
      
      
        and display_name LIKE CONCAT('%',#{displayName},'%') 
      
      
        AND role_name = #{roleName}
      
       
        and role_code  = #{roleCode}
      
      
          AND create_time > #{beginTime} 
      
      
          AND create_time <  #{endTime} 
      
      
        order by ${sidx} ${sord}
